def sqrt(n):
    low = 0
    high = n

    while low <= high:
        mid = (low + high) // 2
        if mid * mid == n:
            return mid
        elif mid * mid < n:
            low = mid + 1
        else:
            high = mid - 1

    return low - 1

def printPrime(n): 
    # Initialize all entries of boolean array as true 
    prime = [True for i in range(n+1)] 
    p = 2
    while (p * p <= n): 
        if (prime[p] == True): 
            for i in range(p * p, n+1, p): 
                prime[i] = False
        p += 1
    # Print all prime numbers 
    print("First",n,"Prime numbers:")
    for p in range(2, n): 
        if prime[p]:  
            print(p, end = ' ') 

